    phi_harmonics = [1.0, PHI, PHI**2, PHI**3]
    for freq in phi_harmonics:
        idx = int(freq * len(thought_spectrum) / max(phi_harmonics))
        if idx < len(thought_spectrum) - 1:
            if thought_spectrum[idx] * thought_spectrum[idx + 1] < 0:
                return True
    return False
